package day16.Assignment;

public class Displayloop3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        System.out.println("Hello");
        for(int i=0; i<=5;i++)   {
        	System.out.println("Hello "+ i);
        	
        	
        }for (int i=5; i>=0;--i)   {
        	System.out.println("Hello "+i);
        }
	}

}
